package myPackage;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//While
		int i =1;
		while (i<=10) {
			System.out.println("Roshita");
			
			i++;
			
		}
		//Do While
		int x=1;
		do {
			System.out.println("My name is Roshita");
			x= x+1;
			
		} while (x<=10);
		
		//Do While incremental and decremental
				int y=10;
				do {
					System.out.println(y);
					y= y-1;
					
				} while (y>0);
		
	}

}
